import { useRef, useEffect, useState } from 'react'
import './App.css'

type Size = {
  height: number
  width: number
}
const App = () => {
  const [size, setSize] = useState<Size>({ height: 1000, width: 1000 })
  const container = useRef<any>()
  useEffect(() => {
    setSize({
      height: container.current.clientHeight,
      width: container.current.clientWidth,
    })
  })
  return (
    <div className="App" ref={container}>
      {JSON.stringify(size)}
      {size && <canvas height={size.height} width={size.height} />}
    </div>
  )
}

export default App
