type Coord = { x: number; y: number; dx: number; dy: number }
type Size = { height: number; width: number }
const FRICTION = 0.999
export type State = { pos: Array<Coord>; size: Size }

const iterate = (bound: Size) => (coord: Coord) => {
  const dx =
    (coord.x + 50 > bound.width || coord.x < 50 ? -coord.dx : coord.dx) *
    FRICTION
  const dy =
    (coord.y + 50 > bound.height || coord.y < 50 ? -coord.dy : coord.dy) *
    FRICTION
  return {
    x: coord.x + dx,
    y: coord.y + dy,
    dx,
    dy,
  }
}

export const click =
  (state: State) =>
  (event: any): State => {
    const { offsetX, offsetY } = event
    console.log(offsetX, offsetY)
    return state
  }

export const step = (state: State) => ({
  ...state,
  pos: state.pos.map(iterate(state.size)),
})
