import { useRef, useEffect } from 'react'

type State = { pos: { x: number; y: number } }
const initialState: State = { pos: { x: 123, y: 123 } }

const initCanvas = (state: State) => (canvas: HTMLCanvasElement) => {
  const ctx = canvas.getContext('2d')
  if (!ctx) return
  requestAnimationFrame(() => {
    step()
  })
}

const clear = (ctx: CanvasRenderingContext2D) => {
  const { height, width } = ctx.canvas
  ctx.fillStyle = 'white'
  ctx.fillRect(0, 0, width, height)
}

const step = (state: State) => ({
  pos: { x: state.pos.x + 1, y: state.pos.y + 1 },
})

const drawCirle =
  ({ x, y }: { x: number; y: number }) =>
  (ctx: CanvasRenderingContext2D) => {
    ctx.beginPath()
    ctx.fillStyle = 'red'
    ctx.arc(x, y, 50, 0, 2 * Math.PI)
    ctx.stroke()
  }

const render = ({
  ctx,
  state,
}: {
  ctx: CanvasRenderingContext2D
  state: State
}) => {
  clear(ctx)
  drawCirle(state.pos)(ctx)
}

const Canvas = ({ height, width }: { height?: number; width?: number }) => {
  const ref = useRef<any>()
  const state = useRef<State>(initialState)

  useEffect(() => {
    if (ref.current) initCanvas(state.current)(ref.current)
  }, [])
  return <canvas {...{ height, width, ref }} />
}

export default Canvas
