type Coord = { x: number; y: number; dx: number; dy: number }
type Size = { height: number; width: number }

export type State = { pos: Array<Coord>; size: Size }

const iterate = (bound: Size) => (coord: Coord) => {
  const dx =
    coord.x + 50 > bound.width || bound.width < 50 ? -coord.dx : coord.dx
  const dy =
    coord.y + 50 > bound.height || bound.hei < 50 ? -coord.dy : coord.dy
  return {
    x: coord.x + dx,
    y: coord.y + dy,
    dx,
    dy,
  }
}

export const step = (state: State) => ({
  ...state,
  pos: state.pos.map(iterate(state.size)),
})
